SELECT CAR_NAME, CAR_MANUFACTUR, CAR_TYPE, CAR_PASSENGER, CAR_FUEL, MTOT, ACTOT, BRANCH_NAME, CAR_KIND_PRICE_WEEK, INSURANCE_PRICE, TOT, BRANCH_NO, CAR_NO, INSURANCE_NO, CAR_KIND_NO, CAR_KIND_NAVI, CAR_KIND_SENSOR, CAR_KIND_BLUETOOTH, CAR_KIND_BLACKBOX, CAR_KIND_SUNROOF, CAR_KIND_CAMERA, CAR_KIND_NONSMOKE FROM CAR NATURAL JOIN( SELECT BRANCH_NO, CAR_NO, INSURANCE_NO, CAR_KIND_NO, ACTOT, BRANCH_NAME, CAR_KIND_PRICE_WEEK, INSURANCE_PRICE, TOT, MTOT, CAR_KIND_NAVI, CAR_KIND_SENSOR, CAR_KIND_BLUETOOTH, CAR_KIND_BLACKBOX, CAR_KIND_SUNROOF, CAR_KIND_CAMERA, CAR_KIND_NONSMOKE FROM BRANCH NATURAL JOIN( SELECT BRANCH_NO, CAR_NO, INSURANCE_NO, CAR_KIND_NO, ACTOT, CAR_KIND_COUNT, (CAR_KIND_PRICE_WEEK+INSURANCE_PRICE)AS TOT, CAR_KIND_NAVI, CAR_KIND_SENSOR, CAR_KIND_BLUETOOTH, CAR_KIND_BLACKBOX, CAR_KIND_SUNROOF, CAR_KIND_CAMERA, CAR_KIND_NONSMOKE, MIN(CAR_KIND_PRICE_WEEK+INSURANCE_PRICE) OVER(PARTITION BY CAR_NO) AS MTOT, CAR_KIND_PRICE_WEEK, INSURANCE_PRICE FROM INSURANCE NATURAL JOIN( SELECT BRANCH_NO, CAR_NO, INSURANCE_NO, CAR_KIND_NO, SUM(AC) OVER(PARTITION BY CAR_NO) ACTOT, CAR_KIND_COUNT, CAR_KIND_PRICE_WEEK, CAR_KIND_NAVI, CAR_KIND_SENSOR, CAR_KIND_BLUETOOTH, CAR_KIND_BLACKBOX, CAR_KIND_SUNROOF, CAR_KIND_CAMERA, CAR_KIND_NONSMOKE FROM CAR_KIND NATURAL JOIN( SELECT BRANCH_NO, CAR_NO, INSURANCE_NO, CAR_KIND_NO, COUNT(CAR_INFO_NO) AC FROM CAR_INFO WHERE NOT EXISTS( SELECT * FROM RENT_RESERV WHERE CAR_INFO.BRANCH_NO=RENT_RESERV.BRANCH_NO AND CAR_INFO.CAR_NO=RENT_RESERV.CAR_NO AND CAR_INFO.INSURANCE_NO=RENT_RESERV.INSURANCE_NO AND CAR_INFO.CAR_KIND_NO=RENT_RESERV.CAR_KIND_NO AND CAR_INFO.CAR_INFO_NO=RENT_RESERV.CAR_INFO_NO AND RENT_RESERV_END
>
TO_DATE(#{rent_reserve_startDateTime},'yyyy-mm-dd hh24:mi') AND RENT_RESERV_START
<
TO_DATE(#{rent_reserve_endDateTime},'yyyy-mm-dd hh24:mi')) AND CAR_INFO_RENT_AT='y' GROUP BY BRANCH_NO,CAR_NO,INSURANCE_NO,CAR_KIND_NO )
CAR_KIND_NAVI=#{car_kind_navi}
AND CAR_KIND_SENSOR=#{car_kind_sensor}
AND CAR_KIND_BLACKBOX=#{car_kind_blackbox}
AND CAR_KIND_BLUETOOTH=#{car_kind_bluetooth}
AND CAR_KIND_SUNROOF=#{car_kind_sunroof}
AND CAR_KIND_CAMERA=#{car_kind_camera}
AND CAR_KIND_NONSMOKE=#{car_kind_nonsmoke}
) WHERE INSURANCE_NAME='자차' ORDER BY MTOT,TOT ) )
CAR_NAME=#{car_name}
