SELECT CAR_NAME, CAR_MANUFACTURER, CAR_TYPE, CAR_PASSENGER, CAR_FUEL, MTOT, SUM(AC) OVER(PARTITION BY CAR_NO) ACTOT, AC, BRANCH_NAME, CAR_KIND_PRICE_WEEK, INSURANCE_PRICE, INSURANCE_LIMIT, TOT, BRANCH_NO, CAR_NO, INSURANCE_NO, CAR_KIND_NO, CAR_KIND_NAVI, CAR_KIND_SENSOR, CAR_KIND_BLUETOOTH, CAR_KIND_BLACKBOX, CAR_KIND_SUNROOF, CAR_KIND_CAMERA, CAR_KIND_NONSMOKE FROM CAR NATURAL JOIN (SELECT BRANCH_NAME, AC, CAR_KIND_PRICE_WEEK, INSURANCE_PRICE, INSURANCE_LIMIT, MTOT, TOT, BRANCH_NO, CAR_NO, INSURANCE_NO, CAR_KIND_NO, CAR_KIND_NAVI, CAR_KIND_SENSOR, CAR_KIND_BLUETOOTH, CAR_KIND_BLACKBOX, CAR_KIND_SUNROOF, CAR_KIND_CAMERA, CAR_KIND_NONSMOKE FROM BRANCH NATURAL JOIN (select AC, CAR_KIND_PRICE_WEEK, INSURANCE_PRICE, INSURANCE_LIMIT, MIN(CAR_KIND_PRICE_WEEK+INSURANCE_PRICE) OVER(PARTITION BY CAR_NO) AS MTOT, (CAR_KIND_PRICE_WEEK * #{weekTime}+CAR_KIND_PRICE_WEEKEND * #{weekendTime} + INSURANCE_PRICE * #{period}) AS TOT, BRANCH_NO, CAR_NO, INSURANCE_NO, CAR_KIND_NO, CAR_KIND_NAVI, CAR_KIND_SENSOR, CAR_KIND_BLUETOOTH, CAR_KIND_BLACKBOX, CAR_KIND_SUNROOF, CAR_KIND_CAMERA, CAR_KIND_NONSMOKE from INSURANCE natural join (select K.*, K.CAR_KIND_TOTAL-(NVL(J1.MUC,0))AC from CAR_KIND K, (select CAR_KIND_NO, MAX(UC) MUC from (select CAR_KIND_NO, D.DA, count(*)UC from RENT_RESERV V, (SELECT (TO_DATE(#{rent_reserve_startDate},'yyyy-mm-dd') + LEVEL -1)DA FROM DUAL CONNECT BY LEVEL <= (TO_DATE(#{rent_reserve_endDate},'yyyy-mm-dd') - TO_DATE(#{rent_reserve_startDate},'yyyy-mm-dd') +1))D where RENT_RESERV_END > DA AND RENT_RESERV_START < (DA+1) group by CAR_KIND_NO, D.DA order by D.DA ) group by CAR_KIND_NO )J1
J1.CAR_KIND_NO (+)= K.CAR_KIND_NO
AND CAR_KIND_NAVI=#{car_kind_navi}
AND CAR_KIND_SENSOR=#{car_kind_sensor}
AND CAR_KIND_BLACKBOX=#{car_kind_blackbox}
AND CAR_KIND_BLUETOOTH=#{car_kind_bluetooth}
AND CAR_KIND_SUNROOF=#{car_kind_sunroof}
AND CAR_KIND_CAMERA=#{car_kind_camera}
AND CAR_KIND_NONSMOKE=#{car_kind_nonsmoke}
)J2 WHERE INSURANCE_NAME='자차'
AND CAR_KIND_NO = #{car_kind_no}
ORDER BY MTOT ) )
CAR_NAME=#{car_name}
